import xml.etree.ElementTree as ET

def parse_xml(xml_data):
    """
    Parses the given XML data and returns a nested dictionary representation.
    """
    def parse_element(element):
        parsed = {}
        if element.text and element.text.strip():
            parsed[element.tag] = element.text.strip()
        for child in element:
            child_parsed = parse_element(child)
            if child.tag not in parsed:
                parsed[child.tag] = child_parsed
            else:
                if type(parsed[child.tag]) is list:
                    parsed[child.tag].append(child_parsed)
                else:
                    parsed[child.tag] = [parsed[child.tag], child_parsed]
        return parsed

    root = ET.fromstring(xml_data)
    return parse_element(root)

